import React, {useState} from 'react'
import './Login.css'
import {
  CAlert,
  CButton,
  CCard,
  CCardBody,
  CCardGroup,
  CCol,
  CContainer,
  CForm,
  CInput,
  CInputGroup,
  CInputGroupPrepend,
  CInputGroupText, CLabel,
  CRow
} from '@coreui/react'
import CIcon from '@coreui/icons-react'
import Header from "../../users/Header";
import {useHistory} from "react-router-dom";
import validator from "validator";

class Login extends React {
  // eslint-disable-next-line react-hooks/rules-of-hooks
  history = useHistory();
  email = '';
  password = '';

  // [email, setEmail] = useState("");
  // [password, setPassword] = useState("");
  // if(localStorage.getItem('token')){
  //   history.push('/');
  // }
  LoginUser = (e) => {

    e.preventDefault();
    if (this.email && this.password) {
      this.props.loginUser({email:this.email, password:this.password})
    }
    document.getElementById('can_not_login').style.opacity = '1';
    setTimeout(() => {
      document.getElementById('can_not_login').style.opacity = '0';
      if (localStorage.getItem('token')) {
        this.history.push('/');
      }
    }, 1500);
  }

  render() {

    return (
      <>
        <Header state={this.props.state}/>
        <CAlert className={this.props.state.user ?
          'alert-success w-50 m-auto text-center' : `alert-danger w-50 m-auto text-center`}
                id='can_not_login'>{this.props.state.user.token ? "Login successful" : 'Incorrect Email or Password'}</CAlert>
        <CContainer>
          <CRow className="justify-content-center">
            <CCol md="8">
              <CCardGroup>
                <CCard className="p-4">
                  <CCardBody>
                    <CForm onSubmit={this.LoginUser}>
                      <h1>Login</h1>
                      <p className="text-muted">Sign In to your account</p>
                      <CLabel className='alert-danger mb-0' id='alert_not_email'>
                        This is not email
                      </CLabel>
                      <CInputGroup className="mb-3">
                        <CInputGroupPrepend>
                          <CInputGroupText>
                            <CIcon name="cil-user"/>
                          </CInputGroupText>
                        </CInputGroupPrepend>
                        <CInput type="text" placeholder="Email"
                                onChange={(e) => {
                                  if (validator.isEmail(e.target.value)) {
                                    this.setEmail(e.target.value)
                                    document.getElementById('alert_not_email').style.opacity = '0'
                                  } else {
                                    this.setEmail("");
                                    document.getElementById('alert_not_email').style.opacity = '1'
                                  }
                                }}/>
                      </CInputGroup>
                      <CInputGroup className="mb-4">
                        <CInputGroupPrepend>
                          <CInputGroupText>
                            <CIcon name="cil-lock-locked"/>
                          </CInputGroupText>
                        </CInputGroupPrepend>
                        <CInput type="password"
                                autoComplete="password"
                                placeholder="Password"
                                minLength='6'
                                onChange={(e) => {
                                  if (e.target.value.length >= 6) {
                                    this.setPassword(e.target.value);
                                  } else {
                                    this.setPassword("");
                                  }
                                }}/>
                      </CInputGroup>
                      <CRow>
                        <CCol xs="6">
                          <CButton color="primary" className="px-4" type='submit' block>Login</CButton>
                        </CCol>
                      </CRow>
                    </CForm>
                  </CCardBody>
                </CCard>
              </CCardGroup>
            </CCol>
          </CRow>
        </CContainer>
      </>
    )
  }
}

export default Login
