import React, {useState} from 'react'
import {
  CButton,
  CCard,
  CCardBody,
  CCol,
  CContainer,
  CForm,
  CInput,
  CInputGroup,
  CInputGroupPrepend,
  CInputGroupText,
  CRow,
  CLabel,
  CAlert
} from '@coreui/react'
import CIcon from '@coreui/icons-react'
import Header from "./Header";
import validator from "validator";
import {useHistory} from "react-router-dom";

const Setting = (props) => {
  const history = useHistory();
  const [name, setName] = useState("");
  const [surname, setSurname] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [repeat_password, setRepeatPassword] = useState("");

  function createUser(e) {
    e.preventDefault();
    document.getElementById('alert_repeated_incorrectly').style.opacity = '0'
    if (password !== repeat_password) {
      document.getElementById('alert_repeated_incorrectly').style.opacity = '1'
    } else if (name && surname && email && password && repeat_password) {
      props.state.register = {name, surname, email, password};
      props.createUser();
      document.getElementById('can_not_create').style.opacity = '1';
    } else {
      props.state.responseRegister = {status: 'Can not Create User'};
      document.getElementById('can_not_create').style.opacity = '1';
    }
    setTimeout(() => {
      document.getElementById('can_not_create').style.opacity = '0';
      if(JSON.parse(localStorage.getItem('status')) === 'Created successful'){
        localStorage.removeItem('status');
        history.push('/login');
      }
    }, 1500);
  }
  return (
    <>
      <Header state = {props.state}/>
      <CAlert className={props.state.responseRegister.status === 'Created successful' ?
        'alert-success w-50 m-auto text-center' : `alert-danger w-50 m-auto text-center`}
              id='can_not_create'>{props.state.responseRegister.status}</CAlert>

      <CCard>
        <CCardHeader>
          Collapse
          <DocsLink name="CCollapse"/>
        </CCardHeader>
        <CCollapse show={collapse}>
          <CCardBody>
            <p>
              Anim pariatur cliche reprehenderit,
              enim eiusmod high life accusamus terry richardson ad squid. Nihil
              anim keffiyeh helvetica, craft beer labore wes anderson cred
              nesciunt sapiente ea proident.
            </p>
            <p>
              Donec molestie odio id nisi malesuada, mattis tincidunt velit egestas. Sed non pulvinar risus. Aenean
              elementum eleifend nunc, pellentesque dapibus arcu hendrerit fringilla. Aliquam in nibh massa. Cras
              ultricies lorem non enim volutpat, a eleifend urna placerat. Fusce id luctus urna. In sed leo tellus.
              Mauris tristique leo a nisl feugiat, eget vehicula leo venenatis. Quisque magna metus, luctus quis
              sollicitudin vel, vehicula nec ipsum. Donec rutrum commodo lacus ut condimentum. Integer vel turpis
              purus. Etiam vehicula, nulla non fringilla blandit, massa purus faucibus tellus, a luctus enim orci non
              augue. Aenean ullamcorper nisl urna, non feugiat tortor volutpat in. Vivamus lobortis massa dolor, eget
              faucibus ipsum varius eget. Pellentesque imperdiet, turpis sed sagittis lobortis, leo elit laoreet arcu,
              vehicula sagittis elit leo id nisi.
            </p>
          </CCardBody>
        </CCollapse>
        <CCardFooter>
          <CButton
            color="primary"
            onClick={toggle}
            className={'mb-1'}
          >Toggling button</CButton>
        </CCardFooter>
      </CCard>
    </>
  )
}

export default Setting













